namespace WebApplicationFlowSync.services.EmailService
{
    public class EmailTemplateBuilder
    {
        public static string BuildTemplate(string title, string message, string? linkText = null, string? linkUrl = null)
        {
            var linkHtml = string.Empty;

            if (!string.IsNullOrEmpty(linkUrl))
            {
                linkHtml = $@"
                    <p style='margin-top: 20px;'>
                        <a href='{linkUrl}' style='background-color: #4CAF50; color: white; padding: 10px 20px;
                            text-decoration: none; border-radius: 5px;'>{linkText ?? "Click here"}</a>
                    </p>";
            }

            return $@"
        <html>
        <body style='font-family: Arial, sans-serif; background-color: #f9f9f9; padding: 20px; direction: ltr;'>
            <div style='max-width: 600px; margin: auto; background-color: #fff; padding: 20px;
                border-radius: 8px; box-shadow: 0 0 10px rgba(0,0,0,0.05);'>

                <!-- Header with logo and name -->
                <div style='display: flex; align-items: center; margin-bottom: 20px;'>
                    <img src='                    <img src=data:image/jpeg;base64,"" alt='Logo' style='height: 40px; margin-right: 10px; />
' alt='Logo' style='height: 40px; margin-right: 10px;' />
                    <span style='font-size: 24px; font-weight: bold; color: #2e7d32;'>FlowSync</span>
                </div>

                <!-- Line below header -->
                <hr style='border: none; border-top: 1px solid #e0e0e0; margin-bottom: 20px;' />

                <!-- Title -->
                <h2 style='color: #2e7d32; text-align: left;'>{title}</h2>

                <!-- Message -->
                <p style='font-size: 16px; color: #333; text-align: left;'>{message}</p>

                {linkHtml}

                <!-- Footer -->
                <p style='font-size: 12px; color: #888; margin-top: 30px; text-align: left;'>
                    © {DateTime.Now.Year} FlowSync. All rights reserved.
                </p>
            </div>
        </body>
        </html>";
        }
    }
}
